<?php

namespace App\Plugins;

/**
 * Enables App\User model to return a dummy avatar if
 * avatar is missing.
 */
trait EnableDummyAvatar {


    public function getAvatarAttribute($avatar)
    {
        return $avatar ?? $this->getDummyAvatar();
    }


    private function getDummyAvatar()
    {
        return 'data:image/svg+xml;base64,';
    }

}